CREATE TABLE recipe(recipe_id INT NOT NULL PRIMARY KEY GENERATED ALWAYS AS IDENTITY (START WITH 1, INCREMENT BY 1), creation_date TIMESTAMP  NOT NULL, veg BOOLEAN, suitable_for INT, instructions VARCHAR(255) NOT NULL);
CREATE TABLE ingredients(ing_id INT NOT NULL PRIMARY KEY GENERATED ALWAYS AS IDENTITY (START WITH 1, INCREMENT BY 1), item VARCHAR(255) NOT NULL, recipe_id INT REFERENCES  recipe(recipe_id));